import React, { Component } from 'react';
import NewsItem from './NewsItem';

export class News extends Component {
  constructor() {
    super();
    this.state = {
      articles: [],
      loading: false,
      page: 1,
      totalResults: 0, // Add totalResults to the initial state
    };
  }

  async componentDidMount() {
    let url = "https://newsapi.org/v2/everything?q=apple&from=2023-06-17&to=2023-06-17&sortBy=popularity&apiKey=b2cfc53535de49c7a363e047a4ca2389&page=1&pageSize=18";
    let data = await fetch(url);
    let passedData = await data.json();
    this.setState({
      articles: passedData.articles,
      totalResults: passedData.totalResults, // Assign the totalResults from the fetched data
    });
  }

  handleNextClick = async () => {
    console.log("next");
    if (this.state.page >= Math.ceil(this.state.totalResults / 18)) {
      // No action needed when reaching the last page
    } else {
      let url = `https://newsapi.org/v2/everything?q=apple&from=2023-06-17&to=2023-06-17&sortBy=popularity&apiKey=b2cfc53535de49c7a363e047a4ca2389&page=${this.state.page + 1}&pageSize=18`;
      let data = await fetch(url);
      let passedData = await data.json();
      this.setState({
        articles: passedData.articles,
        totalResults: passedData.totalResults,
        page: this.state.page + 1,
      });
    }
  };

  handlePreviousClick = async () => {
    console.log("previous");
    let url = `https://newsapi.org/v2/everything?q=apple&from=2023-06-17&to=2023-06-17&sortBy=popularity&apiKey=b2cfc53535de49c7a363e047a4ca2389&page=${this.state.page - 1}&pageSize=18`;
    let data = await fetch(url);
    let passedData = await data.json();
    this.setState({
      articles: passedData.articles,
      page: this.state.page - 1,
    });
  };

  render() {
    return (
      <div className="container my-3">
        <h2>NewsMonkey - Top Headlines</h2>
        <div className="row">
          {this.state.articles.map((element) => {
            return (
              <div className="col-md-4" key={element.url}>
                <NewsItem
                  title={element.title}
                  description={element.description}
                  imageUrl={element.urlToImage}
                  newsUrl={element.url}
                />
              </div>
            );
          })}
        </div>
        <div className="d-flex justify-content-between">
          <button
            disabled={this.state.page <= 1}
            className="btn btn-dark"
            onClick={this.handlePreviousClick}
          >
            &larr; Previous
          </button>
          <button className="btn btn-dark" onClick={this.handleNextClick}>
            Next &rarr;
          </button>
        </div>
      </div>
    );
  }
}

export default News;
