import React, { Component } from 'react'
import NewsItem from './NewsItem'
import Spinner from './Spinner';

export class News extends Component {
    constructor (){
        super ();
        this.state = {
            articles: [],
            loading: false,
            page: 1,
            totalResults: []
        }
    }
    async componentDidMount(){
        let url= `https://newsapi.org/v2/everything?q=apple&from=2023-06-17&to=2023-06-17&sortBy=popularity&apiKey=b2cfc53535de49c7a363e047a4ca2389&page=1&pageSize=${this.props.pageSize}`;
        this.setState({loading: true});
        let data= await fetch(url);
        let passedData = await data.json();
        this.setState({
            articles: passedData.articles, totalResults: passedData.totalResults,
        })
    }

    handleNextClick= async ()=> {
        console.log("next")
            let url= `https://newsapi.org/v2/everything?q=apple&from=2023-06-17&to=2023-06-17&sortBy=popularity&apiKey=b2cfc53535de49c7a363e047a4ca2389&page=${this.state.page + 1 }&pageSize=${this.props.pageSize}`;
            this.setState({loading: true,})
            let data= await fetch(url);
            let passedData = await data.json();
            this.setState({
                articles: passedData.articles,
                page: this.state.page + 1,
                totalResults: passedData.totalResults,
                loading: false,
            })
        
        
    }
    handlePreviousClick= async ()=>{
        console.log("previous")
        let url= `https://newsapi.org/v2/everything?q=apple&from=2023-06-17&to=2023-06-17&sortBy=popularity&apiKey=b2cfc53535de49c7a363e047a4ca2389&page=${this.state.page - 1 }&pageSize=${this.props.pageSize}`;
        let data= await fetch(url);
        this.setState({loading: true})
        let passedData = await data.json();
        this.setState({
            articles: passedData.articles, 
            page: this.state.page - 1,
            loading: false 
        })
    }

  render() {
    return (
      <div className='container my-3'>
        <h1 className='text-center'>NewsMonkey - Top Headlines</h1>
        {this.state.loading &&<Spinner/>}
        <div className="row">
        {this.state.articles.map((element)=>{
            return (<div className="col-md-4" key={element.url}>
                <NewsItem title = {element.title} description = {element.description} imageUrl ={element.urlToImage} newsUrl= {element.url}/>
            </div>)    
        })}
        </div>
        <div className='d-flex justify-content-between'>
            <button disabled={this.state.page<=1} className="btn btn-dark " onClick={this.handlePreviousClick}>&larr; Previous</button>
            <button disabled={this.state.page + 1 > Math.ceil(90/this.props.pageSize)} className="btn btn-dark" onClick={this.handleNextClick}>Next &rarr;</button>
        </div>
       </div>
    )
  }
}

export default News
