import React, { Component } from 'react'
import NewsItem from './NewsItem'

export class News extends Component {
    constructor (){
        super ();
        this.state = {
            articles: [],
            loading: false,
            page: 1
        }
    }
    async componentDidMount(){
        let url= "https://newsapi.org/v2/everything?q=apple&from=2023-06-17&to=2023-06-17&sortBy=popularity&apiKey=b2cfc53535de49c7a363e047a4ca2389";
        let data= await fetch(url);
        let passedData = await data.json();
        this.setState({
            articles: passedData.articles
        })
    }

  render() {
    return (
      <div className='container my-3'>
        <h2>NewsMonkey - Top Headlines</h2>
        <div className="row">
        {this.state.articles.map((element)=>{
            return <div className="col-md-4" key={element.url}>
                <NewsItem title = {element.title} description = {element.description} imageUrl ={element.urlToImage} newsUrl= {element.url}/>
            </div>    
        })}
        </div>
        <div className='d-flex justify-content-between'>
            <button className="btn btn-dark ">Previous</button>
            <button className="btn btn-dark">Next</button>
        </div>
       </div>
    )
  }
}

export default News
